import React, { useEffect } from 'react';
import axios from 'axios';

const UserList = ({ users, setUsers }) => {
  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/User');
      setUsers(response.data);
    } catch (error) {
      console.error('Erro ao buscar usuários:', error);
    }
  };

  const editUser = async (index) => {
    const user = users[index];
    const newName = prompt('Enter new name:', user.name);
    const newEmail = prompt('Enter new email:', user.email);

    if (newName && newEmail) {
      try {
        const response = await axios.put(`http://localhost:5000/User/${user.id}`, {
          name: newName,
          email: newEmail,
        });
        const updatedUsers = [...users];
        updatedUsers[index] = response.data;
        setUsers(updatedUsers);
      } catch (error) {
        console.error('Erro ao editar usuário:', error);
      }
    }
  };

  const deleteUser = async (index) => {
    const user = users[index];
    if (confirm('Tem certeza de deletar o usuário?')) {
      try {
        await axios.delete(`http://localhost:5000/User/${user.id}`);
        setUsers(users.filter((_, i) => i !== index));
      } catch (error) {
        console.error('Erro ao deletar usuário:', error);
      }
    }
  };

  return (
    <div>
      <h2>Lista de Usuários</h2>
      <table>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Email</th>
            <th>Editar</th>
            <th>Excluir</th>
          </tr>
        </thead>
        <tbody>
          {Array.isArray(users) && users.map((user, index) => (
            <tr key={index}>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>
                <button onClick={() => editUser(index)}>Editar</button>
              </td>
              <td>
                <button onClick={() => deleteUser(index)}>Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserList;
