import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Modal from 'react-bootstrap/Modal'; // Corrigido o import do Modal
import Button from 'react-bootstrap/Button';

const UserList = ({ users, setUsers }) => {
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [userToDelete, setUserToDelete] = useState(null);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:5002/User');
      setUsers(response.data);
    } catch (error) {
      console.error('Erro ao buscar usuários:', error);
    }
  };

  const editUser = async (index) => {
    const user = users[index];
    const newName = prompt('Enter new name:', user.name);
    const newEmail = prompt('Enter new email:', user.email);

    if (newName && newEmail) {
      try {
        const response = await axios.put(`http://localhost:5002/User/${user.id}`, {
          name: newName,
          email: newEmail,
        });
        const updatedUsers = [...users];
        updatedUsers[index] = response.data;
        setUsers(updatedUsers);
      } catch (error) {
        console.error('Erro ao editar usuário:', error);
      }
    }
  };

  const confirmDeleteUser = (index) => {
    setUserToDelete(users[index]);
    setShowDeleteModal(true);
  };

  const handleDeleteUser = async () => {
    if (userToDelete) {
      try {
        await axios.delete(`http://localhost:5002/User/${userToDelete.id}`);
        setUsers(users.filter((user) => user.id !== userToDelete.id));
        setUserToDelete(null);
        setShowDeleteModal(false);
      } catch (error) {
        console.error('Erro ao deletar usuário:', error);
      }
    }
  };

  return (
    <div>
      <h2>Lista de Usuários</h2>
      <table>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Email</th>
            <th>Editar</th>
            <th>Excluir</th>
          </tr>
        </thead>
        <tbody>
          {Array.isArray(users) && users.map((user, index) => (
            <tr key={user.id}> {/* Usar user.id como chave única */}
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>
                <button onClick={() => editUser(index)}>Editar</button>
              </td>
              <td>
                <button onClick={() => confirmDeleteUser(index)}>Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      
      {/* Modal de confirmação de exclusão */}
      <Modal show={showDeleteModal} onHide={() => setShowDeleteModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Confirmar Exclusão</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Tem certeza de que deseja excluir o usuário "{userToDelete ? userToDelete.name : ''}"?
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowDeleteModal(false)}>
            Cancelar
          </Button>
          <Button variant="danger" onClick={handleDeleteUser}>
            Excluir
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default UserList;
