import React from 'react';

const UserList = ({ users, editUser, deleteUser }) => {
  return (
    <div>
      <h2>Lista de Usu√°rios</h2>
      <table>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Email</th>
            <th>Editar</th>
            <th>Excluir</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user, index) => (
            <tr key={index}>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>
                <button onClick={() => editUser(index)}>Editar</button>
              </td>
              <td>
                <button onClick={() => deleteUser(index)}>Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserList;
