import React, { useState } from 'react';
import axios from 'axios';

const UserForm = ({ addUser }) => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !email) return;
    try {
      const response = await axios.post('http://localhost:5002/User', { name, email });
      addUser(response.data); // Adiciona o novo usuário à lista existente
      setName(''); // Limpa o campo de nome após a adição do usuário
      setEmail(''); // Limpa o campo de email após a adição do usuário
    } catch (error) {
      console.error('Erro ao adicionar usuário:', error);
    }
  };

  return (
    <div>
      <h2>Criar Usuário</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nome"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <button type="submit">Adicionar Usuário</button>
      </form>
    </div>
  );
};

export default UserForm;
